    def test_get_block(self):
        block = self.eth_blockchain.get_block(12297619)
        self.assertEqual(block["hash"], HexBytes('0x4f4c153028694c2f4855bd46c4665b536924993167a5b537cdc3ed681d5dd75b'))

    def test_get_factory_pools(self):
        univ3 = self.factory_contracts["uniswapv3"]

        # Ethereum Pool V3
        contract = self.eth_blockchain.get_contract(univ3["contract"], abi=univ3["abi"])
        pools = self.eth_blockchain.get_factory_pools(
            contract, argument_filters={"token1": "0x8ee325ae3e54e83956ef2d5952d3c8bc1fa6ec27"})
        self.assertEqual(pools[0]["token1"], "0x8EE325AE3E54e83956eF2d5952d3C8Bc1fa6ec27")

        # Ethereum Pool V2
        univ2 = self.factory_contracts["uniswapv2"]
        contract = self.eth_blockchain.get_contract(univ2["contract"], abi=univ2["abi"])
        pools = self.eth_blockchain.get_factory_pools(
            contract, argument_filters={"token1": "0xd47bDF574B4F76210ed503e0EFe81B58Aa061F3d"})
        self.assertEqual(pools[0]["token1"], "0xd47bDF574B4F76210ed503e0EFe81B58Aa061F3d")

        # Arbitrum Pool
        contract = self.arbitrum_blockchain.get_contract(univ3["contract"], abi=univ3["abi"])
        pools = self.arbitrum_blockchain.get_factory_pools(
            contract, argument_filters={"token1": "0xc8ccbd97b96834b976c995a67bf46e5754e2c48e"})
        self.assertEqual(pools[0]["token1"], "0xc8CCBd97b96834b976C995a67BF46e5754e2C48E")

        uniswapv3poly = self.factory_contracts["uniswapv3poly"]
        # Polygon Pool on Uniswap
        contract = self.polygon_blockchain.get_contract(uniswapv3poly["contract"], abi=uniswapv3poly["abi"])
        pools = self.polygon_blockchain.get_factory_pools(
            contract, argument_filters={"token0": "0xe5417af564e4bfda1c483642db72007871397896"})
        self.assertEqual(pools[0]["token0"], "0xE5417Af564e4bFDA1c483642db72007871397896")

        quickswapv3 = self.factory_contracts["quickswapv3"]
        # Polygon Pool on Quickswap
        contract = self.polygon_blockchain.get_contract(quickswapv3["contract"], abi=quickswapv3["abi"])
        pools = self.polygon_blockchain.get_factory_pools(
            contract, argument_filters={"token0": "0x229b1b6c23ff8953d663c4cbb519717e323a0a84"})
        self.assertEqual(pools[0]["token0"], "0x229b1b6C23ff8953D663C4cBB519717e323a0a84")

        camelotv3 = self.factory_contracts["camelotv3"]
        # Arbitrum Pool on Camelot dex v3
        contract = self.arbitrum_blockchain.get_contract(camelotv3["contract"], abi=camelotv3["abi"])
        pools = self.arbitrum_blockchain.get_factory_pools(
            contract, argument_filters={"token1": "0xf19547f9ed24aa66b03c3a552d181ae334fbb8db"})
        self.assertEqual(pools[0]["token1"], "0xF19547f9ED24aA66b03c3a552D181Ae334FBb8DB")

        camelotv2 = self.factory_contracts["camelotv2"]
        # Arbitrum Pool on Camelot dex v2
        contract = self.arbitrum_blockchain.get_contract(camelotv2["contract"], abi=camelotv2["abi"])
        pools = self.arbitrum_blockchain.get_factory_pools(
            contract, argument_filters={"token1": "0xf19547f9ed24aa66b03c3a552d181ae334fbb8db"})
        self.assertEqual(pools[0]["token1"], "0xF19547f9ED24aA66b03c3a552D181Ae334FBb8DB")

        sushiswapv3polygon = self.factory_contracts["sushiswapv3polygon"]
        # Polygon pool on Sushiwap v3
        contract = self.polygon_blockchain.get_contract(sushiswapv3polygon["contract"], abi=sushiswapv3polygon["abi"])
        pools = self.polygon_blockchain.get_factory_pools(
            contract, argument_filters={"token0": "0x071ac29d569a47ebffb9e57517f855cb577dcc4c"})
        self.assertEqual(pools[0]["token0"], "0x071AC29d569a47EbfFB9e57517F855Cb577DCc4C")

        sushiswapv2eth = self.factory_contracts["sushiswapv2eth"]
        # Ethereum pool on Sushiwap v3
        contract = self.eth_blockchain.get_contract(sushiswapv2eth["contract"], abi=sushiswapv2eth["abi"])
        pools = self.eth_blockchain.get_factory_pools(
            contract, argument_filters={"token0": "0x4104b135dbc9609fc1a9490e61369036497660c8"})
        self.assertEqual(pools[0]["token0"], "0x4104b135DBC9609Fc1A9490E61369036497660c8")

    def test_get_swap_event(self):
        data = HexBytes('0x000000000000000000000000000000000000000000000000000000008f0d1800')
        topics = ['0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef', '0x00000000000000000000000071660c4005ba85c37ccec55d0c4493e66fe775d3', '0x0000000000000000000000007853b3736edba9d7ce681f2a90264307694f97f2']
        event = self.eth_blockchain.get_swap_event(data, topics)
        print(event)

    def test_query_filter(self):
        from_block = 18967710
        to_block = from_block + 100
        filter_object = self.eth_blockchain.w3.eth.get_logs
        logs = self.eth_blockchain._query_filter(filter_object, fromBlock=from_block, toBlock=to_block)
        logs = list(logs)[0][0]
        self.assertEqual(logs["transactionHash"],
                         HexBytes('0xcc6be465bd4a083f342097d663b4d9108de69c2f79f047739cf648dfe7a465ea'))






