    def setUp(self):
        self.explore_eth = Explorer("eth")
        self.explore_bsc = Explorer("bsc")
        self.test_wallet = "0xC05189824bF36f2ad9d0f64a222c1C156Df28DA1"
        self.eth_test_hash = "0x5aca3af227b511ec21b801c97616442039674bca46936c4ee3ab4359067c9ae6"
        self.bsc_test_hash = "0x31342f70f8f379999a22ca91fc6595f3b24b841f8019f21f966be84467fc4c5a"
        self.BSC_USDC_CONTRACT = "0x8ac76a51cc950d9822d68b83fe1ad97b32cd580d"
        self.BSC_USDC_CONTRACT_PROXY = "0xba5fe23f8a3a24bed3236f05f2fcf35fd0bf0b5c"
        self.BSC_USDT_CONTRACT = "0x55d398326f99059ff775485246999027b3197955"
        self.ETH_USDC_CONTRACT = "0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48"
        self.ETH_USDT_CONTRACT = "0xdac17f958d2ee523a2206206994597c13d831ec7"

    def test_convert_from_hex(self):
        num = "0x00000000000000000000000000000000000000000000000000000004a817c800"
        num = self.explore_eth.convert_from_hex(num)
        self.assertEqual(num, 20000)

    def test_convert_to_checksum_address(self):
        address = b'\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\xfe\xa8V\x91/ \xbcO|\x87|R\xd6\n,\xdcy|n\xf8'
        address = self.explore_eth.convert_to_checksum_address(address)
        self.assertEqual(address.lower(), "0xfea856912f20bc4f7c877c52d60a2cdc797c6ef8")

